import React , { Component } from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import { Icon } from 'antd';
import * as actionCreators from '@common/store/actionCreators';
import './style.less';

class Footer extends Component{
    constructor(){
        super();
        this.state = {
            volumeIcon: 'üîä',
            modeIcon: <span role="img" aria-label="list" title="ÂàóË°®Âæ™ÁéØ">üîÅ</span>,
            ppIcon: 'play-circle'
        }
    }
    toPlay = () =>{
        this.audio.play();
        this.setState({ppIcon:'pause-circle'})
    }
    toPause = () =>{
        this.audio.pause();
        this.setState({ppIcon:'play-circle'})
    }
    togglePlay = () =>{
        if(this.audio.paused || this.audio.ended){
            this.toPlay();
            console.log(this.props.playQueue)
        }else{
            this.toPause()
        }
    }
    render(){
        const { volumeIcon, modeIcon, ppIcon} = this.state;
        const { song } = this.props.playQueue;
        return(
            <footer key="player">
                <audio
                    key="audio"
                    src={song.url}
                    ref={(node) =>{
                        this.audio=node
                    }}
                    >
                    ÊÇ®ÁöÑÊµèËßàÂô®‰∏çÊîØÊåÅaudioÊ†áÁ≠æÔºåÊó†Ê≥ïÊí≠ÊîæÈü≥‰πê
                </audio>
                <div className="player-album">
                    <Link to={{ pathname:`/song/${song.id}`}}>
                        <img src={song.album.picUrl} alt="album-img"/>
                    </Link>
                </div>
                <div className="player-btns">
                    <Icon type="backward" />
                    <Icon type={ppIcon} onClick={this.togglePlay} />
                    <Icon type="forward" />
                </div>
                <div className="player-state">
                    <div className="player-state-top">
                        <span className="name">{song.name}</span>
                        <div className="artist">
                            <Link to="">
                                {"Ëä±Á≤•"}
                            </Link>
                        </div>
                        <div className="duration">
                        00:00/3:04
                        </div>
                    </div>

                    <div className="player-state-bottom">
                        <div
                            className="progress-bar"
                            ref={(node) =>{
                                this.progressBar = node;
                            }}
                        >
                            <div className="current-progress" style={{width:`10%`}}/>
                        </div>
                    </div>
                </div>

                <div className="vol-wrapper">
                    <div className="vol">
                        <span>{volumeIcon}</span>
                        <div
                            className="vol-bar"
                            ref={(node) =>{
                                this.valbar=node
                            }}
                        >
                            <div className="current-vol" style={{width:`10%`}} />
                        </div>
                    </div>
                </div>

                <div className="player-extra">
                    <div className="mode-title">
                        {modeIcon}
                    </div>
                    <Icon type="heart-o"/>
                    <Icon type="bars"/>
                </div>
            </footer>
        )
    }
}
const mapState = (state) =>{
    return{
        playQueue: state.playQueue
    }
};
const mapDispatch = (dispatch) =>({
    indexChangeSong(){
        dispatch(actionCreators.changeSong());
    }
});
export default connect(mapState,mapDispatch)(Footer);
