}
class LyricBlock extends Component{
    constructor(props){
        super(props);
        this.state={
            lyrics:[]
        }
    }
    componentDidMount(){
        axios.get(`http://localhost:4000/lyric?id=419375586`).then((res)=>{
            // console.log(res.data.lrc.lyric);
            this.setState({
                lyrics:res.data.lrc.lyric
            });
        })
    }
    //使用正则表达式匹配符合的歌词
    render(){
        return (
            <div className="lyric-block">
                {this.state.lyrics}
            </div>
        )
    }
}
export default LyricBlock;
