import React , { Component } from 'react';
import { withRouter } from 'react-router-dom';
import { Icon, Input } from 'antd';
import menuInfo from '../../router/menu'
import './style.less';

const getTitle = (m, p) =>{
    const matchMenu = m.filter(v => p.indexOf(v.path) !== -1);
    return (matchMenu[0] && matchMenu[0].title) || '歌词'
}
class Header extends Component{
    constructor(props){
        super(props);
        this.state = {
            value: ""
        }
    }
    goBack = () => {
        const { history } = this.props;
        history.goBack();
        }
    selectSongS = (name) =>{
        this.setState({
            value: name
        })
        console.log(this.input);
    }
    render(){
        const {pathname} = this.props.location;
        const title = getTitle(menuInfo, pathname);
        return(
            <header>
                <div className="left">
                    <Icon type="arrow-left" onClick={this.goBack}/>
                    <h2 className="title">{title}</h2>
                </div>
                <Input
                    className="inputText"
                    size="small"
                    onChange={this.selectSongS}
                    ref={(node) =>{
                        this.input = node;
                    }}
                />
                <Icon type="search" />
                <Icon type="close" className="right" />
            </header>
        )
    }
}
export default withRouter(Header);
