import axios from 'axios';
import actionTypes from './actionTypes';

const getPlaylistInfoAction = playlist =>({
    type: actionTypes.PLAYLIST_INFO,
    payload: playlist
});
export const fetchLyric = id => (dispatch) =>{
    axios.get(`http://localhost:4000/playlist/detail?id=${id}`).then((res)=>{
        if(res.data.code){
            dispatch(getPlaylistInfoAction(res.data.playlist));
        }
        console.log(res.data.code);
    })
};
