import axios from 'axios';
import { CARD_LIST, SWIPER_IMG } from './actionTypes'
import { topPlaylist, bannerUrl, test } from '../../../config/api'

export const getCardListAction = list =>({
    type: CARD_LIST,
    payload: { list }
})

export const fetchCardList = (limit) => (dispatch) => {
    axios.get('https://api.imjad.cn/cloudmusic/?type=playlist&id=309390784').then((res) =>{
        const { data } = res;
        console.log(data.playlist);
        const result1 = data.playlist
        if(data.code === 200) {
            dispatch(getCardListAction(data.playlists))
        }else {
            console.log(data.code);
        }
    })

}
export const getList = () =>{
    return (dispatch) =>{
        axios.get(topPlaylist(35)).then((res) =>{
            const { data } = res;
            if(data.code === 200) {
                dispatch(getCardListAction(data.playlists))
            }
        })
    }
}

export const getSwiperImgAction = swiperImg => ({
    type: SWIPER_IMG,
    payload: swiperImg
});

export const fetchSwiperImg = () => (dispatch) =>{
    axios.get(bannerUrl).then((res) =>{
        const { data } = res;
        dispatch(getSwiperImgAction(data.banners))
    })
}
